import Post from '../Post'
import InputBox from '../InputBox';

function ChatRoom({users, posts}) {
  return (
    <>
      {
        posts.map(post => (
          <Post
            key={post.id}
            post={post}
            user={users.find(user => user.id === post.user)}
          />
        ))
      }

      <InputBox />
    </>
  );
}

export default ChatRoom;
