import Post from '../Post';
import { submitPost } from '../../helpers';
import InputBox from '../InputBox';

function ChatRoom({users, posts}) {
  const addPost = (newPost) => {
    submitPost({
      user: 4, // assuming user id is 4
      message: value,
      ts: Date.now()
    })
  }

  return (
    <>
      {
        posts.map(post => (
          <Post
            key={post.id}
            post={post}
            user={users.find(user => user.id === post.user)}
          />
        ))
      }

      <InputBox submit={addPost} />
    </>
  );
}

export default ChatRoom;
