import { Box, Text } from 'grommet';
import Post from '../Post'
import InputBox from '../InputBox';
import styles from  './ChatRoom.module.css';

function ChatRoom({users, posts}) {
  console.log(posts);

  return (
    <Box className={styles.ChatRoom} >
      <Text>Chat Room</Text>
      {
        posts.map(post => (
          <Post
            key={post.id}
          />
        ))
      }

      <InputBox />
    </Box>
  );
}

export default ChatRoom;
