import { Box, Form, Text, TextInput } from 'grommet';
import { Send } from 'grommet-icons';
import useInputState from '../../hooks/useInputState'
import styles from './InputBox.module.css';

function InputBox() {
  const { value, setValue, resetValue, charactersLeft } = useInputState('', 140);

  const submit = () => {
    resetValue()
  }

  return (
    <div className={styles.InputBox} direction="row" >
      <Text size="xsmall" className={styles.submit} color="#aaa">{charactersLeft}</Text>

      <Form onSubmit={submit}>
        <TextInput className={styles.textBox}
          value={value}
          size="small" 
          placeholder="what's happening"
          onChange={setValue}
          width="100%"
        />
      </Form>


      <Send className={styles.submit} onClick={submit} />
    </div>
  );
}

export default InputBox;
