import posts from '../../../seed/posts.json'

export default (req, res) => {
  if (req.method === 'POST') {
    console.log('post request here!', req.body)
    const allPosts = [...posts, req.boody]
    res.status(200).json(allPosts);
  } 
  else {
    res.status(200).json(posts);
  }

}
