import { Grommet } from 'grommet';
import absoluteUrl from 'next-absolute-url';
import ChatRoom from '../components/ChatRoom';

const CURRENT_USER_ID = "current_user" // placeholder for current user's id ;
const CURRENT_USER = { 
  "id": CURRENT_USER_ID
} // placeholder for current user


function App({users, posts}) {
  const allUsers=[...users, CURRENT_USER];

  return (
    <div className="App">
      <Grommet>
        <ChatRoom 
          users={allUsers} 
          posts={posts} 
        />
      </Grommet>
    </div>
  );
}

// fetch on server-side for SSR
App.getInitialProps = async function({req}) {
  const { protocol, host } = absoluteUrl(req);
  const response =  await fetch(`${protocol}//${host}/api/chatroom`);
  const  json = await response.json();
  const { users, posts } = json;
  return { users, posts };
}

export default App;
