import { useState } from 'react';
import Post from '../Post';
import { submitPost } from '../../helpers';
import InputBox from '../InputBox';

const CURRENT_USER_ID = "current_user" // placeholder for current user's id ;
const CURRENT_USER = { 
  "id": CURRENT_USER_ID
} // placeholder for current user

function ChatRoom({posts: initialPosts, users}) {
  const [posts, setPosts] = useState(initialPosts);

  const addPost = (message) => {
    submitPost({
      message,
      ts: Date.now(),
      user: CURRENT_USER_ID 
    })
    .then(allPosts => {
      setPosts(allPosts);
    })
  }

  const postUser = (post) => {
    const {user: userId} = post;
    if (userId === CURRENT_USER_ID) {
      return {};
    }
    return users.find(user => user.id === userId)
  }

  return (
    <>
      {
        posts.map(post => (
          <Post
            key={post.id}
            post={post}
            user={postUser(post)}
            byCurrentUser={post.user === CURRENT_USER_ID}
          />
        ))
      }

      <InputBox submit={addPost} />
    </>
  );
}

export default ChatRoom;
