import  Link from 'next/link';
import { New } from 'grommet-icons';
import TimeAgo from '../TimeAgo';
import styles from  './Card.module.css';

function Card({post, user, byCurrentUser}) {
  if(!post) return null;

  const { message, ts } = post;
  const { real_name, username, verified} = user;

  return (
    <div className={styles.Card}>
      <div className={styles.metadata}>
        <div>
          {
            !byCurrentUser &&
            <span>
              {real_name} - <Link href="#"><a className={styles.username}>@{username}</a></Link>

              {
                verified && 
                <span title="verified">
                  <New className={styles.verified} />
                </span>
              }
            </span>
          }
        </div>
        <div>
          <TimeAgo timestamp={ts} />
        </div>
      </div>

      <p className={styles.message}>
        {message}
      </p>
    </div>
  );
}

export default Card;
