import { Grommet } from 'grommet';
// import { fetcher } from '../helpers';
import ChatRoom from '../components/ChatRoom';

function App({data, error}) {
  return (
    <div className="App">
      <Grommet>
        <ChatRoom data={data} error={error} />
      </Grommet>
    </div>
  );
}

// fetch on server-side before SSR
App.getInitialProps = async function({req}) {
  // const origin = req.get('origin');
  debugger
  // console.log(req.headers.host);
  // return {}
  const response =  await fetch(`${req.headers.referer}/api/chatroom`);

  // // "await" requests after initializing all requests for better performance.
  // await usersRequest; 
  // await postsRequest;

  const  json = await response.json();
  const { users, posts } = json;
  // const posts = await postsRequest.json();
  // console.log(users, posts);
  console.log({ users, posts });
  return { users, posts };
}

export default App;
