import { Box, Text } from 'grommet';
import  Link from 'next/link';
import moment from 'moment';
// import { fetcher } from '../helpers';
import styles from  './Post.module.css';

function Post({post, user}) {
  if(!post || !user) {
    return null
  }

  const {message, ts } = post;
  const { id, real_name, username, verified} = user;

  return (
    <Box className={styles.Post}>
      <Box>

      </Box>
      <Box>
        <div>
          <Text size="xsmall">
            {real_name} - <Link href="#"><a className={styles.userLink}>@{username}</a></Link>
          </Text>
          <Text size="xsmall">
            {moment(ts).fromNow()}
          </Text>
        </div>
        <Box className={styles.message}>
          <Text size="15px">{message}</Text>
        </Box>
      </Box>
    </Box>
  );
}

export default Post;
