export function submitPost(newPost) {
  fetch('/api/posts', {
    method: 'post',
    body: JSON.stringify(newPost),
    headers: {
      'Accept': 'application/json, text/plain, */*',
      'Content-Type': 'application/json'
    },
  })
  .then(response => response.json())
}
