import  Link from 'next/link';
import moment from 'moment';
import styles from  './Post.module.css';

function Post({post, user, byCurrentUser}) {
  if(!post) {
    return null
  }

  const { message, ts } = post;
  const { real_name, username, verified} = user;

  return (
    <div className={`${styles.Post} ${byCurrentUser ? styles.byCurrentUser:''}`}>
      <div>
        <div className={styles.avatarArea}>
          <img 
            className={styles.avatar} 
            src={byCurrentUser ? '/images/better-icon.svg':`/images/${username}.jpg`}
            alt={`${username}'s avatar`}
          />
        </div>
      </div>

      <div className={styles.messageArea}>
        <div className={styles.metadata}>

            <span>
              {
                !byCurrentUser &&
                <>
                  {real_name} - <Link href="#"><a className={styles.username}>@{username}</a></Link>
                  {/* add verified status */}
                </>

              }
            </span>



          {/* make Age component */}
          {/* {
            !byCurrentUser && */}
            <span> {moment(ts).fromNow()} </span>
          {/* } */}
        </div>

        <p className={styles.message}>
          {message}
        </p>
      </div>
    </div>
  );
}

export default Post;
