import { Stack } from 'grommet';
import { useState } from 'react';
import styles from  './Card.module.css';

function Card({children, back}) {
  const [showBack, setShowBack] = useState(false);

  const updateShowBack = (bool) => {
    // if (back && showBack !== bool) {
    //   setTimeout(()=> setShowBack(bool), 100);
    // }
  }

  return (
    <div className={styles.Card}>
      <Stack >
        {/* <div className={`${styles.back} ${!showBack && styles.hide}`}
          onMouseOver={()=> updateShowBack(false)}
          onMouseOut={()=> updateShowBack(true)}
        >
          {back}
        </div> */}

        <div className={`${styles.front}`}
          onMouseOver={()=> updateShowBack(true)}
          onMouseOut={()=> updateShowBack(false)}
        >
          {children}
        </div>

        <div className={`${styles.back} ${!showBack && styles.hide}`}
          onMouseOver={()=> updateShowBack(false)}
          onMouseOut={()=> updateShowBack(true)}
        >
          {children}
        </div>
      </Stack>


    </div>
  );
}

export default Card;
